    /**
     * APIProperty: labelLonYOffset
     * {Integer} the offset of the longitude (X) label from the bottom of the map.
     */
    labelLonYOffset: 2,
	
    /**
     * APIProperty: labelLatXOffset
     * {Integer} the offset of the latitude (Y) label from the right of the map.
     */
